import React, { useState } from 'react';
import Typewriter from './Typewriter';

function ChatBox() {
  const [message, setMessage] = useState('');
  const [responses, setResponses] = useState([]);
  const [pastedImage, setPastedImage] = useState(null);

  const sendMessage = async () => {
    if (!message && !pastedImage) return;

    const requestBody = {
      message,
      image: pastedImage,
      history: responses.map((r) => ({ role: 'assistant', content: r.response })),
    };

    try {
      const response = await fetch('/api/chat', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(requestBody),
      });

      const data = await response.json();
      setResponses([
        ...responses,
        { query: message, response: data.response, image: data.image },
      ]);
      setMessage('');
      setPastedImage(null); // Clear the pasted image
    } catch (error) {
      console.error('Error:', error);
    }
  };

  const handlePaste = (event) => {
    const items = event.clipboardData.items;
    for (const item of items) {
      if (item.type.startsWith('image/')) {
        const file = item.getAsFile();
        const reader = new FileReader();

        reader.onload = (e) => {
          setPastedImage(e.target.result); // Base64 encoded image
        };

        reader.readAsDataURL(file);
        break; // Stop after the first image
      }
    }
  };

  return (
    <div>
      <h1>
        <Typewriter text="Talk to me about your question or copy your image including your question" speed={40} />
      </h1>

      <textarea
        className="custom-textarea"
        id="question"
        value={message}
        onChange={(e) => setMessage(e.target.value)}
        onPaste={handlePaste}
        rows="4"
        cols="50"
        placeholder="Type your message or paste an image..."
      />

      {pastedImage && (
        <div>
      <h1>
        <Typewriter text="pasted image" speed={40} />
      </h1>
          <img src={pastedImage} alt="Pasted content" style={{ maxWidth: '100%', height: 'auto' }} />
        </div>
      )}

      <button onClick={sendMessage}>Send</button>

      <div>
        {responses.map((item, index) => (
          <div key={index} style={{ marginBottom: '20px' }}>
            <p><b>You:</b> {item.query}</p>
            <p><b>GPT:</b> {item.response}</p>
            {item.image && (
              <img
                src={item.image}
                alt="Generated Visual"
                style={{ maxWidth: '100%', height: 'auto' }}
              />
            )}
          </div>
        ))}
      </div>
    </div>
  );
}

export default ChatBox;
