import React, { useState, useEffect } from 'react';
import './AdvancedCalculator.css'; // Optional for styling

function AdvancedCalculator() {
  const [input, setInput] = useState('');
  const [result, setResult] = useState(null);

  const handleButtonClick = (value) => {
    setInput((prevInput) => prevInput + value);
  };

  const clearInput = () => {
    setInput('');
    setResult(null);
  };

  const backspace = () => {
    setInput((prevInput) => prevInput.slice(0, -1));
  };

  const calculateResult = () => {
    if (!input) {
      setResult('Error'); // Show an error if input is empty
      return;
    }

    try {
      const calcResult = eval(input.replace('^', '**')); // Replace ^ with ** for exponentiation
      setResult(calcResult);
    } catch (error) {
      setResult('Error'); // Handle invalid expressions
    }
  };

  // Handle keyboard input
  const handleKeyDown = (event) => {
    const validKeys = '0123456789+-*/.^()';
    const key = event.key;

    if (validKeys.includes(key)) {
      setInput((prevInput) => prevInput + key);
      event.preventDefault(); // Prevent any default browser behavior
    } else if (key === 'Enter') {
      event.preventDefault(); // Prevent form submission behavior
      calculateResult();
    } else if (key === 'Backspace') {
      event.preventDefault(); // Prevent browser default backspace behavior
      backspace();
    } else if (key === 'Escape') {
      clearInput();
    }
  };

  useEffect(() => {
    window.addEventListener('keydown', handleKeyDown);
    return () => {
      window.removeEventListener('keydown', handleKeyDown);
    };
  }, []);

  return (
    <div className="advanced-calculator">
      <div className="display">
        <div className="input">{input || '0'}</div>
        <div className="result">{result !== null ? `= ${result}` : ''}</div>
      </div>
      <div className="buttons">
        {['7', '8', '9', '/'].map((value) => (
          <button key={value} onClick={() => handleButtonClick(value)}>
            {value}
          </button>
        ))}
        {['4', '5', '6', '*'].map((value) => (
          <button key={value} onClick={() => handleButtonClick(value)}>
            {value}
          </button>
        ))}
        {['1', '2', '3', '-'].map((value) => (
          <button key={value} onClick={() => handleButtonClick(value)}>
            {value}
          </button>
        ))}
        {['0', '.', '=', '+'].map((value) => (
          <button
            key={value}
            onClick={() => (value === '=' ? calculateResult() : handleButtonClick(value))}
          >
            {value}
          </button>
        ))}
        {['sin(', 'cos(', 'tan(', 'log('].map((value) => (
          <button key={value} onClick={() => handleButtonClick(value)}>
            {value}
          </button>
        ))}
        {['π', 'e', '^', '√('].map((value) => (
          <button
            key={value}
            onClick={() =>
              handleButtonClick(value === 'π' ? Math.PI.toString() : value === 'e' ? Math.E.toString() : value)
            }
          >
            {value}
          </button>
        ))}
        <button className="clear-button" onClick={clearInput}>
          Clear
        </button>
        <button className="backspace-button" onClick={backspace}>
          Backspace
        </button>
      </div>
    </div>
  );
}

export default AdvancedCalculator;
