import logo from './logo7.png';
import { useState } from 'react';
import './App.css';
import TopicSelector from './components/TopicSelector';
import PlotGenerator from './components/PlotGenerator';
import Chatbox from './components/chatbox'; 
import Calculator from './components/Calculator';
import ExamGrader from './components/ExamGrader';

import { Button } from "@/components/ui/button";
import { Card, CardHeader, CardTitle, CardContent } from "@/components/ui/card";

function App() {
  const [selectedComponent, setSelectedComponent] = useState(null);

  const renderComponent = () => {
    switch (selectedComponent) {
      case 'TopicSelector':
        return <TopicSelector />;
      case 'PlotGenerator':
        return <PlotGenerator />;
      case 'Chatbox':
        return <Chatbox />;
      case 'Calculator':
        return <Calculator />;
      case 'ExamGrader':
        return <ExamGrader />;
      default:
        return (
          <Card className="text-center p-4 max-w-lg mx-auto">
            <CardHeader>
              <CardTitle className="text-gray-600 text-xl font-light">
                Click on a button to load a component
              </CardTitle>
            </CardHeader>
          </Card>
        );
    }
  };

  return (
    <div className="App bg-gray-50 min-h-screen">
      <header className="App-header py-8">
        {selectedComponent === null && (
          <Card className="text-center max-w-lg mx-auto p-6 bg-white shadow-md">
            <CardHeader>
              <CardTitle className="text-3xl font-semibold text-gray-700">
                Welcome
              </CardTitle>
            </CardHeader>
            <CardContent>
              <p className="text-gray-500">
                Make a choice from these functions
              </p>
            </CardContent>
          </Card>
        )}
        {selectedComponent === null && (
          <img
            src={logo}
            className="App-logo mx-auto mt-6"
            alt="logo"
            style={{ width: "100px", height: "100px" }}
          />
        )}
        <div className="flex justify-center space-x-4 mt-8">
          <Button variant="default" onClick={() => setSelectedComponent('TopicSelector')}>
            Course Helper
          </Button>
          <Button variant="default" onClick={() => setSelectedComponent('PlotGenerator')}>
            Plot Generator
          </Button>
          <Button variant="default" onClick={() => setSelectedComponent('Chatbox')}>
            Chatbox
          </Button>
          <Button variant="default" onClick={() => setSelectedComponent('Calculator')}>
            Calculator
          </Button>
          <Button variant="default" onClick={() => setSelectedComponent('ExamGrader')}>
            Exam Grader
          </Button>
        </div>
        <div className="component-container mt-10">
          {renderComponent()}
        </div>
      </header>
    </div>
  );
}

export default App;
