import logo from './logo7.png';
import { useState } from 'react';
import './App.css';
import TopicSelector from './components/TopicSelector';
import PlotGenerator from './components/PlotGenerator';
import Chatbox from './components/chatbox';
import Calculator from './components/Calculator';
import ExamGrader from './components/ExamGrader';

import Button from './components/ui/button';
import { Card, CardHeader, CardTitle, CardContent } from './components/ui/card';

function App() {
  const [selectedComponent, setSelectedComponent] = useState(null);

  const renderComponent = () => {
    switch (selectedComponent) {
      case 'TopicSelector':
        return <TopicSelector />;
      case 'PlotGenerator':
        return <PlotGenerator />;
      case 'Chatbox':
        return <Chatbox />;
      case 'Calculator':
        return <Calculator />;
      case 'ExamGrader':
        return <ExamGrader />;
      default:
        return (
          <Card className="text-center p-6 max-w-lg mx-auto border border-gray-200 shadow-lg">
            <CardHeader>
              <CardTitle className="text-gray-600 text-lg font-light">
                Click on a button to load a component
              </CardTitle>
            </CardHeader>
          </Card>
        );
    }
  };

  return (
    <div className="App bg-gray-50 min-h-screen flex flex-col items-center justify-center">
      <header className="App-header py-12 w-full max-w-4xl">
        {/* Welcome Section */}
        {selectedComponent === null && (
          <Card className="text-center max-w-lg mx-auto p-8 bg-white shadow-xl">
            <CardHeader>
              <CardTitle className="text-4xl font-bold text-gray-800">
                Welcome
              </CardTitle>
            </CardHeader>
            <CardContent>
              <p className="text-gray-500 text-lg">
                Make a choice from the options below to explore various features.
              </p>
            </CardContent>
          </Card>
        )}
        {selectedComponent === null && (
          <img
            src={logo}
            className="App-logo mx-auto mt-6 rounded-lg shadow-md"
            alt="logo"
            style={{ width: '120px', height: '120px' }}
          />
        )}
        {/* Button Section */}
        <div className="flex flex-wrap justify-center space-x-4 mt-10">
          <Button
            variant="default"
            className="w-48"
            onClick={() => setSelectedComponent('TopicSelector')}
          >
            Course Helper
          </Button>
          <Button
            variant="default"
            className="w-48"
            onClick={() => setSelectedComponent('PlotGenerator')}
          >
            Plot Generator
          </Button>
          <Button
            variant="default"
            className="w-48"
            onClick={() => setSelectedComponent('Chatbox')}
          >
            Chatbox
          </Button>
          <Button
            variant="default"
            className="w-48"
            onClick={() => setSelectedComponent('Calculator')}
          >
            Calculator
          </Button>
          <Button
            variant="default"
            className="w-48"
            onClick={() => setSelectedComponent('ExamGrader')}
          >
            Exam Grader
          </Button>
        </div>
        {/* Component Rendering Section */}
        <div className="component-container mt-12">{renderComponent()}</div>
      </header>
    </div>
  );
}

export default App;
