import logo from './logo.svg';



import { useState } from 'react';
import './App.css';
import TopicSelector from './components/TopicSelector';
import PlotGenerator from './components/PlotGenerator';
import Chatbox from './components/chatbox'; // Assuming this is another component

function App() {
  const [selectedComponent, setSelectedComponent] = useState(null);

  const renderComponent = () => {
    switch (selectedComponent) {
      case 'TopicSelector':
        return <TopicSelector />;
      case 'PlotGenerator':
        return <PlotGenerator />;
      case 'Chatbox':
        return <Chatbox />;
      default:
        return <p>Please select a component to load.</p>;
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Choose a Component</h1>
        <img src={logo} className="App-logo" alt="logo" />
        <div>
          <button className="custom-button-modern"onClick={() => setSelectedComponent('TopicSelector')}>Topic Selector</button>
          <button className="custom-button-modern"onClick={() => setSelectedComponent('PlotGenerator')}>Plot Generator</button>
          <button className="custom-button-modern"onClick={() => setSelectedComponent('Chatbox')}>Chatbox</button>
        </div>
        <div className="component-container">
          {renderComponent()}
        </div>
      </header>
    </div>
  );
}

export default App;
