import React, { useState } from 'react';
import Typewriter from './Typewriter';
function ChatBox() {
    const [message, setMessage] = useState('');
    const [responses, setResponses] = useState([]);

    const sendMessage = async () => {
        if (!message) return;
        try {
            const response = await fetch('/api/chat', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ message })
            });
            const data = await response.json();
            setResponses([...responses, { query: message, response: data.response }]);
            setMessage('');
        } catch (error) {
            console.error('Error:', error);
        }
    };

    return (
        <div>
        <h1>
          <Typewriter text="Talk to me about your question or copy your image including your question" speed={40} />
        </h1>

             <textarea className="custom-textarea"
                            id="question"
                            value={message}
                            onChange={(e) => setMathFunction(e.target.value)}
                            rows="4"
                            cols="50"
                                            placeholder="Type your message..."
                        />
            <button onClick={sendMessage}>Send</button>
            <div>
                {responses.map((item, index) => (
                    <p key={index}><b>You:</b> {item.query} <br /><b>GPT:</b> {item.response}</p>
                ))}
            </div>
        </div>
    );
}

export default ChatBox;
