import React, { useState } from 'react';
import Typewriter from './Typewriter';
const currentUrl = window.location.href;

const ipAddress = currentUrl.split(':')[1].split('/')[2];

function ExamGrader() {
  const [questionImage, setQuestionImage] = useState('');
  const [markSchemeImage, setMarkSchemeImage] = useState('');
  const [studentSolutionImage, setStudentSolutionImage] = useState('');
  const [result, setResult] = useState({ score: '', reason: '' });
  const [loading, setLoading] = useState(false);

  const handlePaste = (event, setImage) => {
    const items = event.clipboardData.items;
    for (const item of items) {
      if (item.type.startsWith('image/')) {
        const file = item.getAsFile();
        const reader = new FileReader();

        reader.onload = (e) => setImage(e.target.result);
        reader.readAsDataURL(file);
        break;
      }
    }
  };

  const handleSubmit = async () => {
    setLoading(true);
    try {
      const response = await fetch('http://' + ipAddress + ':200/api/chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          questionImage,
          markSchemeImage,
          studentSolutionImage,
        }),
      });

      const data = await response.json();
      setResult({ score: data.score, reason: data.reason });
    } catch (error) {
      console.error('Error grading:', error);
    } finally {
      setLoading(false);
    }
  };

  const removeQuestionImage = () => {
    setQuestionImage(null);
};
const removeMarkSchemeImage = () => {
    setMarkSchemeImage(null);
};
const removeStudentSolutionImage = () => {
    setStudentSolutionImage(null);
};
  return (
    <div style={{ display: 'flex', flexDirection: 'row', marginBottom: '20px' }}>
      <textarea
        placeholder="Paste Question Image Here"
        onPaste={(e) => handlePaste(e, setQuestionImage)}
        style={{ width: '30%', marginRight: '10px' }}
      />
                  {questionImage && (
                <div>
                    <h1>
                        <Typewriter text="pasted question image:" speed={20} />
                    </h1>
                    <img src={questionImage} alt="Pasted content" style={{ maxWidth: '30%', height: 'auto' }} />
                    <button onClick={removeQuestionImage} style={{ marginTop: '10px' }}>Remove Pasted Image</button>
                </div>
            )}
      <textarea
        placeholder="Paste Mark Scheme Image Here"
        onPaste={(e) => handlePaste(e, setMarkSchemeImage)}
        style={{ width: '30%', marginRight: '10px' }}
      />
                        {markSchemeImage && (
                <div>
                    <h1>
                        <Typewriter text="pasted mark scheme image:" speed={20} />
                    </h1>
                    <img src={markSchemeImage} alt="Pasted content" style={{ maxWidth: '30%', height: 'auto' }} />
                    <button onClick={removeMarkSchemeImage} style={{ marginTop: '10px' }}>Remove Pasted Image</button>
                </div>
            )}
      <textarea
        placeholder="Paste Student Solution Image Here"
        onPaste={(e) => handlePaste(e, setStudentSolutionImage)}
        style={{ width: '30%', marginRight: '10px' }}
      />
                              {studentSolutionImage && (
                <div>
                    <h1>
                        <Typewriter text="pasted student solution image:" speed={20} />
                    </h1>
                    <img src={studentSolutionImage} alt="Pasted content" style={{ maxWidth: '30%', height: 'auto' }} />
                    <button onClick={removeStudentSolutionImage} style={{ marginTop: '10px' }}>Remove Pasted Image</button>
                </div>
            )}
      <button onClick={handleSubmit} disabled={loading}>
        {loading ? 'Grading...' : 'Submit'}
      </button>

      <div style={{ marginLeft: '20px' }}>
        <div><b>Score:</b> {result.score}</div>
        <div><b>Reason:</b> {result.reason}</div>
      </div>
    </div>
  );
}

export default ExamGrader;
